mapper.select:
select hb.ID, hb.Banner_Title, hb.Url, hb.Links, hb.Indexs, hb.Statu, hb.CreateTime, hb.Opt_Date, hb.Opt_Id,a.adminName from Home_Banner hb LEFT JOIN Admin a on a.ID=hb.Opt_Id
hb.Banner_Title like '%${map.bannerTitle}%'
and hb.CreateTime>=#{map.startTime}
and #{map.endTime}>= hb.CreateTime
and hb.Statu=#{map.statu}
order by hb.CreateTime desc

		{call moveAndDownHomeBanner(
				#{iid ,		 mode=IN,	jdbcType=INTEGER},
				#{flag,		 mode=IN,	jdbcType=INTEGER},
				#{result ,	 mode=OUT,	jdbcType=INTEGER}
    		)}

select hb.ID,   hb.Banner_Title,   hb.Url,   hb.Links,   hb.Indexs,  
hb.Statu,  hb.CreateTime,  hb.Opt_Date, hb.Opt_Id 
from  Home_Banner  hb where  hb.ID=#{id}
