mapper.select:
select ib.ID, ib.Banner_Title, ib.Url, ib.Links, ib.Indexs, ib.Statu, ib.CreateTime, ib.Opt_Date, ib.Opt_Id,a.adminName from IntegralMall_Banner ib LEFT JOIN Admin a on a.ID=ib.Opt_Id
ib.Banner_Title like '%{map.bannerTitle}%'
and ib.CreateTime>=#{map.startTime}
and #{map.endTime}>= ib.CreateTime
and ib.Statu=#{map.statu}
order by ib.CreateTime desc

		{call moveAndDownIntegralMallBanner(
				#{iid ,		 mode=IN,	jdbcType=INTEGER},
				#{flag,		 mode=IN,	jdbcType=INTEGER},
				#{result ,	 mode=OUT,	jdbcType=INTEGER} 
    		)}

select ib.ID,   ib.Banner_Title,   ib.Url,   ib.Links,   ib.Indexs, 
ib.Statu,  ib.CreateTime, ib.Opt_Date, ib.Opt_Id 
from  IntegralMall_Banner ib  where ib.ID=#{id}
