mapper.select:
select rb.ID, rb.Banner_Title, rb.Url, rb.Links, rb.Indexs, rb.Statu, rb.CreateTime, rb.Opt_Date, rb.Opt_Id,a.adminName from Register_Banner rb LEFT JOIN Admin a on a.ID=rb.Opt_Id
rb.Banner_Title like '%{map.bannerTitle}%'
and rb.CreateTime>=#{map.startTime}
and #{map.endTime}>= rb.CreateTime
and rb.Statu=#{map.statu}
order by rb.CreateTime desc

		{call moveAndDownRegisterBanner(
				#{iid ,		 mode=IN,	jdbcType=INTEGER},
				#{flag,		 mode=IN,	jdbcType=INTEGER},
				#{result ,	 mode=OUT,	jdbcType=INTEGER} 
    		)}

select rb.ID,   rb.Banner_Title,   rb.Url,   rb.Links,   rb.Indexs,  
rb.Statu,  rb.CreateTime,  rb.Opt_Date, rb.Opt_Id 
from  Register_Banner  rb  where  rb.ID = #{id}
