mapper.select:
select ci.Commodity_ID,ci.Add_Time,ci.Commodity_NO,ci.Commodity_Name,CommodityType.name1 as CommodityTypename ,ct.Commodity_Classify, ci.Market_Price,ci.Is_VIP_Exclusive,ci.Commodity_Statu, CommodityStatu.name1 as CommodityStatus,ci.Discount,ci.VIP_Discount, ci.Is_Active, (select ifnull(sum(cor.Quantity),0) from Commodity_Order cor left join Order_Records orc on orc.Order_ID = cor.Order_ID where (orc.Order_Type = 3 or orc.Order_Type = 2 ) and cor.Commodity_ID = ci.Commodity_ID ) as sumQuantity,
(select ifnull(sum(cor.Quantity),0) from Commodity_Order cor left join Order_Records orc on orc.Order_ID = cor.Order_ID where orc.Order_Type = 0 and cor.Commodity_ID = ci.Commodity_ID ) as backorder
from Commodity_Info ci left join Commodity_Type ct on ct.Commodity_Type_ID = ci.Commodity_Type_ID left join DICT CommodityType on CommodityType.code1= ct.Commodity_Type and CommodityType.tcode = 'DICT_SPFL' left join DICT CommodityStatu on CommodityStatu.code1= ci.Commodity_Statu and CommodityStatu.tcode = 'DICT_SPZT'
ci.Commodity_Name like '%${map.commodityName}%'
and ci.Commodity_NO=#{map.commodityNO}
and ct.Commodity_Type=#{map.commodityType}
and ci.Commodity_Statu=#{map.commodityStatu}
order by ci.Add_Time desc
order by sumQuantity desc
order by backorder desc

 
	     {call addInventoryrecords(
				#{commodityID  ,	     mode=IN,	jdbcType=BIGINT},
				#{adminID  ,	         mode=IN,	jdbcType=BIGINT},
				#{operationDirection ,	 mode=IN,	jdbcType=INTEGER},
				#{quantitys,	         mode=IN,	jdbcType=INTEGER},
				#{result ,	             mode=OUT,	jdbcType=INTEGER}
    		)}

 
     {call CommodityISPush(
				#{commodityID  ,	     mode=IN,	jdbcType=BIGINT },
				#{isPush  ,	             mode=IN,	jdbcType=INTEGER}, 
				#{result ,	             mode=OUT,	jdbcType=INTEGER} 
    		)}

mapper.update:
update Commodity_Info 
set VIP_Discount = #{vipDiscount}
where  Commodity_ID = #{commodityID}
