mapper.select:
select mat.Attest_Type_ID,mat.Member_Type,mat.Attest_TypeName,mat.Attach_Type,mat.Picture_Url,mat.Need_Check, mat.Can_More,mat.Can_More,mat.Type_Cure,mat.Credit_Score,mat.Statu,mat.Opt_Date,mat.Admin_ID, a.adminName from Member_Attest_Type mat LEFT JOIN Admin a on a.Admin_ID=mat.Admin_ID
mat.Attest_TypeName like '%${map.attestTypeName}%'
and mat.Statu=#{map.statu}
and mat.memberType=#{map.memberType}

select mat.Member_Type,mat.Attest_TypeName,mat.Attach_Type,mat.Picture_Url,mat.Need_Check,
mat.Can_More,mat.Can_More,mat.Type_Cure,mat.Credit_Score,mat.Statu,mat.Opt_Date,mat.Admin_ID
from  Member_Attest_Type mat where Attest_Type_ID=#{attestTypeID}

select mat.Attest_Type_ID,mat.Member_Type,mat.Attest_TypeName,mat.Attach_Type,mat.Picture_Url,mat.Need_Check,
mat.Can_More,mat.Can_More,mat.Type_Cure,mat.Credit_Score,mat.Statu,mat.Opt_Date,mat.Admin_ID
from  Member_Attest_Type mat 
 where 1=1
           
            and mat.Member_Type=#{memberType} 
            
           
            and mat.Statu=#{statu}
